sysauto_script.condition:
function checkCondition(){
	var today = new GlideDateTime(); // get todays date and time
	var dayOfWeek = today.getDayOfWeekLocalTime(); // get the day of the week: 1 for monday , 2 for tuesday ... so on
	return !(dayOfWeek == 6 || dayOfWeek == 7); // return false if its saturday or sunday
}
answer = checkCondition();
answer;

sysauto_script.script:
var tasks = new GlideRecord("x_58872_needit_needit");
tasks.addEncodedQuery("(state!=3^state!=8)&(u_when_needed >=" + gs.beginningOfToday() + "^u_when_needed <=" +gs.endOfTomorrow()+")");
gs.info(tasks.getEncodedQuery());
tasks.query();
while (tasks.next()){
	gs.info("This NeedIT Number: "+tasks.number+ " is dued soon on,  " + tasks.u_when_needed);
}
